        private MovesManager()
        {
            LoadMoves();

            _namesToMoves = new Dictionary<string, MoveData>();
            for (int i = 0; i < MovesCount; i++)
            {
                if (Moves[i].Name != null && !_namesToMoves.ContainsKey(Moves[i].Name))
                {
                    _namesToMoves.Add(Moves[i].Name, Moves[i]);
                }
            }
            for (int i = 0; i < MovesCount; i++)
            {
                string lowerName = MoveNames[i].ToLowerInvariant();
                if (_namesToMoves.ContainsKey(lowerName))
                {
                    _idsToMoves[i] = _namesToMoves[lowerName];
                }
            }
        }
        public MoveData GetMoveData(string moveName)
        {
            if (!string.IsNullOrEmpty(moveName))
            {
                for (int i = 1; i < MovesCount; i++)
                {
                    if (_idsToMoves[i].Name.ToLowerInvariant() == moveName.ToLowerInvariant())
                        return _idsToMoves[i];
                }
            }
            return null;
        }
        public int GetMoveID(string moveName)
        {
            if (!string.IsNullOrEmpty(moveName))
            {
                for (int i = 1; i < MovesCount; i++)
                {
                    if (_idsToMoves[i] != null)
                    {
                        if (_idsToMoves[i].Name.ToLowerInvariant() == moveName.ToLowerInvariant())
                            return i;
                    }
                }
            }
            return -1;
        }
        public MoveData GetMoveData(int moveId)
        {
            if (moveId > 0 && moveId < MovesCount)
            {
                return _idsToMoves[moveId];
            }
            return null;
        }

        private void LoadMoves()
        {
            //[true,0,\"Status\",\"Raises the user\'s Attack by 2.\",\"Swords Dance\",\"Normal\"]
            LoadMoveNames();
            for (int k = 0; k < MovesCount; k++)
            {
                Moves[k] = new MoveData();
            }
            var i = 0;
            foreach (var move in MOVE_DATA.Split(']').ToArray())
            {
                string rlMove = move.Replace("\"\"", "").Replace("\"", "");
                string fc = move;
                if (move.StartsWith(","))
                    fc = move.Substring(1);
                if (rlMove == "") return;
                var moveData = fc.Replace("[", "").Split(new string[] { ",\"" }, StringSplitOptions.None);
                List<string> MoveDataList = new List<string>();
                for (int k = 0; k < moveData.Length; k++)
                {
                    moveData[k] = moveData[k].Replace("\"\"", "").Replace("\"", "");
                    if (moveData[k].Contains(",") && k != 3)
                    {
                        if (!(moveData[k].Contains(" ") && moveData[k].EndsWith(".")))
                        {
                            foreach (var subData in moveData[k].Split(',').ToArray())
                            {
                                MoveDataList.Add(subData);
                            }
                        }
                    }
                    else if (!(moveData[k].Contains(" ") && moveData[k].EndsWith(".")) && k != 2)
                        MoveDataList.Add(moveData[k]);
                }
                if (MoveDataList.Count == 0)
                    MoveDataList = moveData.ToList();
                string moveName = MoveDataList[3] == "" ? $"empty move {i}" : MoveDataList[3].ToLowerInvariant();
                int Acc = 0;
                bool status = false;
                int Power = 0;
                DamageType dmgType = DamageType.Physical;
                string type = "";
                if (MoveDataList[0] == "true" || MoveDataList[2].Replace("\"", "").ToLowerInvariant().Trim() == "status")
                {
                    status = true;
                    Power = -1;
                    if (MoveDataList[0] != "true")
                    {
                        Acc = MoveDataList[0] == "" ? -1 : Convert.ToInt32(MoveDataList[0]);
                    }
                    else
                        Acc = -1;

                    if (MoveDataList[2].ToLowerInvariant().Trim() == DamageType.Physical.ToString().ToLowerInvariant().Trim())
                        dmgType = DamageType.Physical;
                    else if (MoveDataList[2].ToLowerInvariant().Trim() == DamageType.Special.ToString().ToLowerInvariant().Trim())
                        dmgType = DamageType.Special;
                }
                else
                {
                    status = false;
                    Acc = MoveDataList[0] == "" ? -1 : Convert.ToInt32(MoveDataList[0]);
                    Power = MoveDataList[1] == "" ? -1 : Convert.ToInt32(MoveDataList[1]);
                    if (MoveDataList[2].ToLowerInvariant().Trim() == DamageType.Physical.ToString().ToLowerInvariant().Trim())
                        dmgType = DamageType.Physical;
                    else if (MoveDataList[2].ToLowerInvariant().Trim() == DamageType.Special.ToString().ToLowerInvariant().Trim())
                        dmgType = DamageType.Special;
                    else
                    {
                        status = true;
                        Power = -1;
                        if (MoveDataList[0] != "true")
                        {
                            Acc = MoveDataList[0] == "" ? -1 : Convert.ToInt32(MoveDataList[0]);
                        }
                        else
                            Acc = -1;
                    }
                }
                if (Power == 0)
                    Power = -1;
                type = MoveDataList[4].ToLowerInvariant();
                Moves[i].Name = moveName;
                Moves[i].Power = Power;
                Moves[i].Accuracy = Acc;
                Moves[i].Type = type.ToLowerInvariant();
                Moves[i].Status = status;
                Moves[i].DamageType = dmgType;
                i++;
            }
        }
        private void LoadMoveNames()
        {
            //[100,80,\"Special\",\"Charges, then hits foe(s) turn 2. High crit ratio.\",\"Razor Wind\",\"Normal\"]
            //[100,40,\"Special\",\"Power doubles during Fly, Bounce, and Sky Drop.\",\"Gust\",\"Flying\"]
            //[true,195,\"Special\",\"Very high critical hit ratio.\",\"10,000,000 Volt Thunderbolt\",\"Electric\"]
            var movesS = MOVE_IDS.Split(']');
            MoveNames[0] = "empty move 0";
            var i = 1;
            foreach (var move in movesS.ToArray())
            {
                string rlMove = move.Replace("\"\"", "").Replace("\"", "");
                string fc = move;
                if (rlMove == "") return;
                if (rlMove.StartsWith(","))
                    rlMove = rlMove.Substring(1);
                string moveName = fc.Split(new string[] { ",\"" }, StringSplitOptions.None)[1];
                MoveNames[i] = moveName.Replace("\"\"", "").Replace("\"", "");
                i++;
            }
        }
    }
